import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:travel/misc/colors.dart';import 'package:travel/widgets/appText.dart';import 'package:travel/widgets/button.dart';class  WelcomePgae extends StatefulWidget {  const WelcomePgae ({Key? key}) : super(key: key);  @override  _State createState() => _State();}class _State extends State<WelcomePgae> {  List images = [    "welcome-one.png",    "welcome-two.png",    "welcome-three.png",  ];  @override  Widget build(BuildContext context) {    return Scaffold(      body: PageView.builder(          scrollDirection: Axis.vertical,          itemCount: images.length,          itemBuilder: (_, index){            return Container(              width: double.maxFinite,              height: double.maxFinite,              decoration: BoxDecoration(                image: DecorationImage(                  image: AssetImage(                    "images/"+images[index]                  ),                  fit: BoxFit.cover                )              ),              child: Container(                margin: const EdgeInsets.only(top: 150, left: 20, right: 20),                child: Row(                  mainAxisAlignment: MainAxisAlignment.spaceBetween,                  children: [                    Column(                      crossAxisAlignment: CrossAxisAlignment.start,                      children: [                        AppText(text: "Trips", size: 25, weight: FontWeight.bold),                        SizedBox(height: 5), // spacing between two widgets                        AppText(text: 'Mountain', size: 25, color: Colors.black87),                        SizedBox(height: 15),                        Container(                          width: 250,                          child: AppText(text: 'Mountain hikes give you an incredible sense of freedom along with endurance tests',                              size: 20,                              color: AppColors.textColor2                          )                        ),                        SizedBox(height: 15),                        Button(width: 95, imageName: 'button-one.png')                      ],                    ),                    Column(                      children: List.generate(3, (indexDots) => Container(                        margin: const EdgeInsets.only(bottom: 3),                        width: 7,                        height: index == indexDots ? 30 : 8,                        decoration: BoxDecoration(                          borderRadius: BorderRadius.circular(8),                          color: index == indexDots ? AppColors.mainColor : AppColors.mainColor.withOpacity(0.3),                        ),                      )),                    )                  ],                ),              )            );      }),    );  }}